import React, { useState } from 'react';
import { GraphQLClient, gql } from 'graphql-request';
import Autocomplete from 'react-autocomplete';

const client = new GraphQLClient('https://eldenring.fanapis.com/api/graphql');

// Definisci le query dinamiche per diversi tipi di oggetti
const QUERIES = {
  weapon: gql`
    query SearchItems($name: String!) {
      weapon(name: $name) {
        id
        name
        image
        description
      }
    }
  `,
  shield: gql`
    query SearchItems($name: String!) {
      shield(name: $name) {
        id
        name
        image
        description
      }
    }
  `,
  npc: gql`
    query SearchItems($name: String!) {
      npc(name: $name) {
        id
        name
        image
        description
      }
    }
  `,
  armor: gql`
    query SearchItems($name: String!) {
      armor(name: $name) {
        id
        name
        image
        description
      }
    }
  `,
  boss: gql`
    query SearchItems($name: String!) {
      bosses(name: $name) {
        id
        name
        image
        description
      }
    }
  `,
  // Aggiungi altre query per altre categorie come talisman, spirit, ecc.
};

const Sidebar = () => {
  const [searchValue, setSearchValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [itemType, setItemType] = useState('weapon'); // Default type
  const [nodeType, setNodeType] = useState('default'); // Default tipo iniziale
  const [nodeName, setNodeName] = useState('');
  const [nodeImage, setNodeImage] = useState('');
  const [nodeDescription, setNodeDescription] = useState('');
  const [nodeNotes, setNodeNotes] = useState('');

  const onDragStart = (event) => {
    const nodeData = {
      type: nodeType,
      name: nodeName,
      image: nodeImage,
      description: nodeDescription,
      notes: nodeNotes,
    };
    event.dataTransfer.setData('application/reactflow', JSON.stringify(nodeData));
    event.dataTransfer.effectAllowed = 'move';
  };

  // Funzione per cercare gli elementi tramite GraphQL
  const fetchItems = async (name) => {
    try {
      const response = await client.request(QUERIES[itemType], { name });
      const results = response[itemType]; // Accedi all'array di dati corretto (e.g., weapons, shields, npcs)
      setSuggestions(results);
    } catch (error) {
      console.error('Errore nel recupero dei dati:', error);
    }
  };

  // Funzione per aggiornare i suggerimenti durante la digitazione
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchValue(value);
    if (value.length > 2) {
      fetchItems(value);
    }
  };

  // Funzione per gestire la selezione di un oggetto
  const handleSelect = (val) => {
    const selected = suggestions.find((item) => item.name === val);
    setSearchValue(val);
    if (selected) {
      setNodeName((prev) => prev || selected.name); // Imposta solo se vuoto
      setNodeImage((prev) => prev || selected.image); // Imposta solo se vuoto
      setNodeDescription((prev) => prev || selected.description); // Imposta solo se vuoto
    }
  };

  // Funzione per gestire il cambio di tipo di oggetto
  const handleTypeChange = (e) => {
    setItemType(e.target.value);
    setSuggestions([]); // Resetta i suggerimenti
    setSearchValue(''); // Resetta il valore di ricerca
  };

  return (
    <aside>
      <div className="description">Puoi trascinare i nodi a destra.</div>

      {/* Dropdown per selezionare il tipo di oggetto */}
      <div className="dropdown">
        <label>Item Type:</label>
        <select value={itemType} onChange={handleTypeChange}>
          <option value="weapon">Weapon</option>
          <option value="shield">Shield</option>
          <option value="npc">NPC</option>
          <option value="armor">Armor</option>
          <option value="boss">Boss</option>
          {/* Aggiungi altre opzioni per gli altri tipi */}
        </select>
      </div>

      {/* Campo di autocompletamento */}
      <div className="autocomplete">
        <label>Search Item:</label>
        <Autocomplete
          getItemValue={(item) => item.name}
          items={suggestions}
          renderItem={(item, isHighlighted) => (
            <div key={item.id} style={{ background: isHighlighted ? '#eee' : 'transparent' }}>
              {item.name}
            </div>
          )}
          value={searchValue}
          onChange={handleInputChange}
          onSelect={handleSelect}
        />
      </div>

      <div className="description">You can drag these nodes to the pane on the right.</div>

      <select onChange={(e) => setNodeType(e.target.value)} value={nodeType}>
        <option value="input">Input Node</option>
        <option value="default">Default Node</option>
        <option value="output">Output Node</option>
      </select>

      <div>
        <input 
          type="text" 
          placeholder="Node Name" 
          value={nodeName} 
          onChange={(e) => setNodeName(e.target.value)} 
        />
        <input 
          type="text" 
          placeholder="Image URL" 
          value={nodeImage} 
          onChange={(e) => setNodeImage(e.target.value)} 
        />
        <input 
          type="text" 
          placeholder="Description" 
          value={nodeDescription} 
          onChange={(e) => setNodeDescription(e.target.value)} 
        />
        <input 
          type="text" 
          placeholder="Notes" 
          value={nodeNotes} 
          onChange={(e) => setNodeNotes(e.target.value)} 
        />
        <div 
          className="dndnode" 
          onDragStart={onDragStart} 
          draggable 
          style={{ padding: '10px', border: '1px solid #ccc', margin: '5px', cursor: 'grab' }}
        >
          Custom Node
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
