import React from 'react';
import { Handle } from 'reactflow';

const CustomNode = ({ data }) => {
  return (
    <div style={{ 
      padding: '10px', 
      border: '1px solid black', 
      borderRadius: '5px', 
      backgroundColor: '#f9f9f9', 
      width: '150px',
      textAlign: 'center' // Centra il contenuto
    }}>
      <strong>{data.label}</strong>
      {data.image && (
        <img 
          src={data.image} 
          alt={data.label} 
          style={{ 
            width: '100%', // Imposta la larghezza dell'immagine
            height: 'auto', // Mantiene il rapporto d'aspetto
            borderRadius: '5px' // Aggiunge un bordo arrotondato all'immagine
          }} 
        />
      )}
      {data.notes && <p>{data.notes}</p>}

      {/* Aggiungi punti di connessione */}
      <Handle type="target" position="top" />
      <Handle type="source" position="bottom" />
    </div>
  );
};

export default CustomNode;
