import React, { useState, useEffect } from 'react';
import { request, gql } from 'graphql-request';

const API_URL = 'https://eldenring.fanapis.com/api/graphql';

// Query GraphQL per cercare oggetti e restituire solo il campo "name"
const SEARCH_QUERY = gql`
  query SearchItems($name: String!) {
    items(name: $name) {
      data {
        id
        name
        image
        description
      }
    }
  }
`;

const Sidebar = ({ onAddCustomNode }) => {
  const [searchTerm, setSearchTerm] = useState(''); // Termini di ricerca
  const [results, setResults] = useState([]); // Risultati della ricerca
  const [selectedItem, setSelectedItem] = useState(null); // Oggetto selezionato

  // Funzione per cercare gli oggetti con GraphQL
  const fetchItems = async (searchTerm) => {
    if (!searchTerm) return;
    try {
      const data = await request(API_URL, SEARCH_QUERY, { name: searchTerm });
      setResults(data.items.data);
    } catch (error) {
      console.error('Errore nel recupero dei dati:', error);
    }
  };

  // Effettua la ricerca quando il termine cambia
  useEffect(() => {
    if (searchTerm) {
      const timeoutId = setTimeout(() => {
        fetchItems(searchTerm);
      }, 500); // Ritardo per ridurre le chiamate API
      return () => clearTimeout(timeoutId); // Pulisce il timeout precedente
    }
  }, [searchTerm]);

  // Quando un oggetto viene selezionato dall'autocomplete
  const handleSelectItem = (item) => {
    setSelectedItem(item);
    onAddCustomNode(item); // Chiamata per aggiungere un nuovo nodo con i dati selezionati
    setSearchTerm(''); // Resetta il campo di ricerca
    setResults([]); // Nasconde i risultati dopo la selezione
  };

  return (
    <aside>
      <div className="description">Search Elden Ring items and create a node.</div>

      {/* Campo di input per la ricerca */}
      <input
        type="text"
        placeholder="Search Elden Ring items"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Mostra i risultati */}
      {results.length > 0 && (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {results.map((item) => (
            <li
              key={item.id}
              style={{ padding: '10px', borderBottom: '1px solid #ddd', cursor: 'pointer' }}
              onClick={() => handleSelectItem(item)} // Seleziona un item
            >
              <strong>{item.name}</strong>
            </li>
          ))}
        </ul>
      )}
    </aside>
  );
};

export default Sidebar;
