import React from 'react';
import { Handle } from 'reactflow'; // Importa Handle

// Componente per il nodo personalizzato
const CustomNode = ({ data }) => {
  return (
    <div style={{ padding: '10px', border: '1px solid black', borderRadius: '5px', backgroundColor: '#f9f9f9' }}>
      <strong>{data.label}</strong>
      {data.image && <img src={data.image} alt={data.label} style={{ width: '50px', height: '50px' }} />}
      {data.notes && <p>{data.notes}</p>}

      {/* Aggiungi punti di connessione */}
      <Handle type="target" position="top" />
      <Handle type="source" position="bottom" />
    </div>
  );
};

export default CustomNode;
