import React, { useState } from 'react';

export default () => {
  const [selectedNodeType, setSelectedNodeType] = useState('input'); // Stato per il tipo di nodo selezionato

  const onDragStart = (event) => {
    event.dataTransfer.setData('application/reactflow', selectedNodeType);
    event.dataTransfer.effectAllowed = 'move';
  };

  const handleNodeTypeChange = (event) => {
    setSelectedNodeType(event.target.value); // Aggiorna il tipo di nodo selezionato
  };

  return (
    <aside>
      <div className="description">
        You can drag these nodes to the pane on the right.
      </div>
      <div className="node-selector">
        <label htmlFor="node-type">Select Node Type: </label>
        <select id="node-type" value={selectedNodeType} onChange={handleNodeTypeChange}>
          <option value="input">Input Node</option>
          <option value="default">Default Node</option>
          <option value="output">Output Node</option>
        </select>
      </div>
      <div className={`dndnode ${selectedNodeType}`} onDragStart={onDragStart} draggable>
        {`${selectedNodeType.charAt(0).toUpperCase() + selectedNodeType.slice(1)} Node`}
      </div>
    </aside>
  );
};
