import React, { useState } from 'react';

const Sidebar = ({ onNodeAdd }) => {
  const [selectedNodeType, setSelectedNodeType] = useState('input');
  const [nodeName, setNodeName] = useState(''); // Stato per il nome del nodo
  const [nodeNotes, setNodeNotes] = useState(''); // Stato per le note del nodo
  const [nodeImage, setNodeImage] = useState(''); // Stato per l'immagine del nodo

  const handleNodeTypeChange = (event) => {
    setSelectedNodeType(event.target.value);
  };

  const handleNameChange = (event) => {
    setNodeName(event.target.value);
  };

  const handleNotesChange = (event) => {
    setNodeNotes(event.target.value);
  };

  const handleImageChange = (event) => {
    setNodeImage(event.target.value);
  };

  const handleDragStart = (event) => {
    const nodeData = {
      type: selectedNodeType,
      name: nodeName,
      notes: nodeNotes,
      image: nodeImage,
    };
    event.dataTransfer.setData('application/reactflow', JSON.stringify(nodeData));
    event.dataTransfer.effectAllowed = 'move';
  };

  return (
    <aside>
      <div className="description">
        You can drag these nodes to the pane on the right.
      </div>
      <div className="node-selector">
        <label htmlFor="node-type">Select Node Type: </label>
        <select id="node-type" value={selectedNodeType} onChange={handleNodeTypeChange}>
          <option value="input">Input Node</option>
          <option value="default">Default Node</option>
          <option value="output">Output Node</option>
        </select>
      </div>
      <div className="customization">
        <label htmlFor="node-name">Node Name: </label>
        <input
          id="node-name"
          type="text"
          value={nodeName}
          onChange={handleNameChange}
          placeholder="Enter node name"
        />
      </div>
      <div className="customization">
        <label htmlFor="node-notes">Notes: </label>
        <textarea
          id="node-notes"
          value={nodeNotes}
          onChange={handleNotesChange}
          placeholder="Enter notes"
        />
      </div>
      <div className="customization">
        <label htmlFor="node-image">Image URL: </label>
        <input
          id="node-image"
          type="text"
          value={nodeImage}
          onChange={handleImageChange}
          placeholder="Enter image URL"
        />
      </div>
      <div
        className={`dndnode ${selectedNodeType}`}
        onDragStart={handleDragStart}
        draggable
      >
        <span>{nodeName || `${selectedNodeType.charAt(0).toUpperCase() + selectedNodeType.slice(1)} Node`}</span>
        {nodeImage && <img src={nodeImage} alt={nodeName} style={{ width: '50px', height: '50px' }} />}
      </div>
    </aside>
  );
};

export default Sidebar;
