import React, { useState } from 'react';
import { GraphQLClient, gql } from 'graphql-request';
import Autocomplete from 'react-autocomplete';
import './style.css'; // Importa il file CSS
import { QUERIES } from '../Queries/queries';

const client = new GraphQLClient('https://eldenring.fanapis.com/api/graphql');

const Sidebar = ({ onUpdateNode }) => {
  const [searchValue, setSearchValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [itemType, setItemType] = useState(''); // Default type
  const [nodeType, setNodeType] = useState('default'); // Default come tipo iniziale
  const [nodeName, setNodeName] = useState('');
  const [nodeImage, setNodeImage] = useState('');
  const [nodeDescription, setNodeDescription] = useState('');
  const [nodeNotes, setNodeNotes] = useState('');

  const onDragStart = (event) => {
    const nodeData = {
      type: nodeType, // Imposta il tipo
      name: nodeName,
      image: nodeImage,
      description: nodeDescription,
      notes: nodeNotes,
    };
    event.dataTransfer.setData('application/reactflow', JSON.stringify(nodeData));
    event.dataTransfer.effectAllowed = 'move';
  };

  // Funzione per cercare i nomi tramite GraphQL, cambia la query in base al tipo selezionato
  const fetchNames = async (type) => {
    try {
      const response = await client.request(QUERIES[type].allNames);
      const results = response[`${type}`]; // Access the correct data array (e.g., weapons, shields, npcs)
      
      // Estrai solo i nomi e rimuovi i duplicati usando un Set
      const uniqueNames = [...new Set(results.map(item => item.name))];
      setSuggestions(uniqueNames); // Aggiorna i suggerimenti con nomi unici
    } catch (error) {
      console.error('Errore nel recupero dei dati:', error);
    }
  };
  

  // Funzione per gestire il cambio di tipo di oggetto
  const handleTypeChange = async (e) => {
    const selectedType = e.target.value;
    setItemType(selectedType);
    setSearchValue(''); // Resetta il valore di ricerca
    setSuggestions([]); // Resetta i suggerimenti
    setSelectedItem(null); // Resetta l'elemento selezionato

    // Esegui la query per ottenere i nomi degli oggetti
    await fetchNames(selectedType);
  };

  const handleUpdate = () => {
    // Crea un oggetto nodo con i dati aggiornati
    const updatedNodeData = {
      name: nodeName,
      image: nodeImage,
      description: nodeDescription,
      notes: nodeNotes,
      type: nodeType,
    };
    onUpdateNode(updatedNodeData); // Chiama la funzione di aggiornamento passata come props
  };

  // Funzione per aggiornare i suggerimenti durante la digitazione
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchValue(value);

    if (value.length > 2) { // Esegui il filtraggio solo se il valore ha più di 2 caratteri
      const filteredSuggestions = suggestions.filter((item) =>
        item.toLowerCase().includes(value.toLowerCase()) // Confronta in modo case-insensitive
      );
      setSuggestions(filteredSuggestions); // Aggiorna i suggerimenti filtrati
    } else {
      // Se la lunghezza è 2 o meno, resettare i suggerimenti
      fetchNames(itemType); // Riporta i suggerimenti per il tipo selezionato
    }
  };

  // Funzione per gestire la selezione di un oggetto
  const handleSelect = async (val) => {
    setSearchValue(val);
    
    // Fetch complete details for the selected item
    const response = await client.request(QUERIES[itemType].details, { name: val });
    const result = response[itemType][0]; // Prendi il primo risultato che corrisponde al nome
    if (result) {
      setNodeName(result.name);
      setNodeImage(result.image);
      setNodeDescription(result.description);
    }
  };

  return (
    <aside className="sidebar">
      {/* Dropdown per selezionare il tipo di oggetto */}
      <div className="dropdown">
        <label>Item Type:</label>
        <select value={itemType} onChange={handleTypeChange}>
          <option value=""></option>
          <option value="ammo">Ammo</option>
          <option value="armor">Armor</option>
          <option value="ashofwar">Ash Of War</option>
          <option value="boss">Boss</option>
          <option value="creature">Creature</option>
          <option value="incantation">Incantation</option>
          <option value="item">Item</option>
          <option value="npc">NPC</option>
          <option value="shield">Shield</option>
          <option value="sorcery">Sorcery</option>
          <option value="spirit">Spirit</option>
          <option value="talisman">Talisman</option>
          <option value="weapon">Weapon</option>
        </select>
      </div>

      {/* Campo di autocompletamento */}
      <div className="autocomplete">
        <label>Search Item:</label>
        <Autocomplete
          getItemValue={(item) => item}
          items={suggestions}
          renderItem={(item, isHighlighted) => (
            <div
              key={item}
              className={`autocomplete-item ${
                isHighlighted ? "highlighted" : ""
              }`} // Applica classi per lo stile
            >
              {item}
            </div>
          )}
          value={searchValue}
          onChange={handleInputChange}
          onSelect={handleSelect}
          inputProps={{
            className: "autocomplete-input", // Applica la classe per l'input
          }}
          wrapperStyle={{ position: "relative" }} // Imposta lo stile del wrapper
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Node Name"
          value={nodeName}
          onChange={(e) => setNodeName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Image URL"
          value={nodeImage}
          onChange={(e) => setNodeImage(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={nodeDescription}
          onChange={(e) => setNodeDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Notes"
          value={nodeNotes}
          onChange={(e) => setNodeNotes(e.target.value)}
        />

        <button onClick={handleUpdate} className="btn">Update Node</button>

        <div className="description">
          You can drag these nodes to the pane on the right.
        </div>

        <div className="dropdown">
          <label>Node Type:</label>
          <select
            className="dropdown"
            onChange={(e) => setNodeType(e.target.value)}
            value={nodeType}
          >
            <option value="input">Input Node</option>
            <option value="default">Default Node</option>
            <option value="output">Output Node</option>
            {/* Aggiungi altre opzioni per gli altri tipi */}
          </select>
        </div>
        <div
          className="dndnode"
          onDragStart={onDragStart}
          draggable
          style={{
            padding: "10px",
            border: "1px solid #ccc",
            margin: "5px",
            cursor: "grab",
          }}
        >
          Custom Node
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
